#pragma once

/**
 * NGAP ASN.1 Wrapper
 *
 * This is a simplified wrapper for NGAP (3GPP TS 38.413) ASN.1 definitions.
 * In a production environment, these would be generated by asn1c compiler
 * from the official ASN.1 specifications.
 */

#include <cstdint>

namespace callflow {
namespace asn1 {

/**
 * NGAP Procedure Codes (3GPP TS 38.413)
 */
enum class NgapProcedureCode : uint8_t {
    AMF_CONFIGURATION_UPDATE = 0,
    AMF_STATUS_INDICATION = 1,
    CELL_TRAFFIC_TRACE = 2,
    DEACTIVATE_TRACE = 3,
    DOWNLINK_NAS_TRANSPORT = 4,
    DOWNLINK_NON_UE_ASSOCIATED_NRPPA_TRANSPORT = 5,
    DOWNLINK_RAN_CONFIGURATION_TRANSFER = 6,
    DOWNLINK_RAN_STATUS_TRANSFER = 7,
    DOWNLINK_UE_ASSOCIATED_NRPPA_TRANSPORT = 8,
    ERROR_INDICATION = 9,
    HANDOVER_CANCEL = 10,
    HANDOVER_NOTIFICATION = 11,
    HANDOVER_PREPARATION = 12,
    HANDOVER_RESOURCE_ALLOCATION = 13,
    INITIAL_CONTEXT_SETUP = 14,
    INITIAL_UE_MESSAGE = 15,
    LOCATION_REPORTING_CONTROL = 16,
    LOCATION_REPORTING_FAILURE_INDICATION = 17,
    LOCATION_REPORT = 18,
    NAS_NON_DELIVERY_INDICATION = 19,
    NG_RESET = 20,
    NG_SETUP = 21,
    OVERLOAD_START = 22,
    OVERLOAD_STOP = 23,
    PAGING = 24,
    PATH_SWITCH_REQUEST = 25,
    PDU_SESSION_RESOURCE_MODIFY = 26,
    PDU_SESSION_RESOURCE_MODIFY_INDICATION = 27,
    PDU_SESSION_RESOURCE_NOTIFY = 28,
    PDU_SESSION_RESOURCE_RELEASE = 29,
    PDU_SESSION_RESOURCE_SETUP = 30,
    PRIVATE_MESSAGE = 31,
    PWS_CANCEL = 32,
    PWS_FAILURE_INDICATION = 33,
    PWS_RESTART_INDICATION = 34,
    RAN_CONFIGURATION_UPDATE = 35,
    RAN_CONFIGURATION_UPDATE_ACKNOWLEDGE = 36,
    RRC_INACTIVE_TRANSITION_REPORT = 37,
    REROUTE_NAS_REQUEST = 38,
    TRACE_FAILURE_INDICATION = 39,
    TRACE_START = 40,
    UE_CONTEXT_MODIFICATION = 41,
    UE_CONTEXT_RELEASE = 42,
    UE_CONTEXT_RESUME = 43,
    UE_CONTEXT_SUSPEND = 44,
    UE_RADIO_CAPABILITY_CHECK = 45,
    UPLINK_NAS_TRANSPORT = 46,
    UPLINK_NON_UE_ASSOCIATED_NRPPA_TRANSPORT = 47,
    UPLINK_RAN_CONFIGURATION_TRANSFER = 48,
    UPLINK_RAN_STATUS_TRANSFER = 49,
    UPLINK_UE_ASSOCIATED_NRPPA_TRANSPORT = 50,
    WRITE_REPLACE_WARNING = 51
};

/**
 * NGAP Criticality
 */
enum class NgapCriticality : uint8_t {
    REJECT = 0,
    IGNORE = 1,
    NOTIFY = 2
};

/**
 * NGAP Message Type
 */
enum class NgapMessageType : uint8_t {
    INITIATING_MESSAGE = 0,
    SUCCESSFUL_OUTCOME = 1,
    UNSUCCESSFUL_OUTCOME = 2
};

/**
 * NGAP Cause Group
 */
enum class NgapCauseGroup : uint8_t {
    RADIO_NETWORK = 0,
    TRANSPORT = 1,
    NAS = 2,
    PROTOCOL = 3,
    MISC = 4
};

/**
 * NGAP Information Element IDs
 */
enum class NgapIeId : uint16_t {
    AMF_UE_NGAP_ID = 10,
    RAN_UE_NGAP_ID = 85,
    NAS_PDU = 38,
    CAUSE = 15,
    PDU_SESSION_RESOURCE_SETUP_LIST = 74,
    PDU_SESSION_RESOURCE_RELEASE_LIST = 72,
    GUAMI = 27,
    ALLOWED_NSSAI = 0,
    UE_AGGREGATE_MAXIMUM_BIT_RATE = 110,
    CORE_NETWORK_ASSISTANCE_INFORMATION = 19,
    PDU_SESSION_ID = 88,
    QOS_FLOW_SETUP_REQUEST_LIST = 136,
    USER_LOCATION_INFORMATION = 121,
    ROUTING_ID = 90,
    S_NSSAI = 94
};

}  // namespace asn1
}  // namespace callflow
