#pragma once

/**
 * X2AP ASN.1 Wrapper
 *
 * This is a simplified wrapper for X2AP (3GPP TS 36.423) ASN.1 definitions.
 * In a production environment, these would be generated by asn1c compiler
 * from the official ASN.1 specifications.
 */

#include <cstdint>

namespace callflow {
namespace asn1 {

/**
 * X2AP Procedure Codes (3GPP TS 36.423)
 */
enum class X2apProcedureCode : uint8_t {
    HANDOVER_PREPARATION = 0,
    HANDOVER_CANCEL = 1,
    LOAD_INDICATION = 2,
    ERROR_INDICATION = 3,
    SN_STATUS_TRANSFER = 4,
    UE_CONTEXT_RELEASE = 5,
    X2_SETUP = 6,
    RESET = 7,
    ENB_CONFIGURATION_UPDATE = 8,
    RESOURCE_STATUS_REPORTING = 9,
    MOBILITY_SETTINGS_CHANGE = 10,
    RADIO_LINK_FAILURE_INDICATION = 11,
    HANDOVER_REPORT = 12,
    CELL_ACTIVATION = 13
};

/**
 * X2AP Criticality
 */
enum class X2apCriticality : uint8_t {
    REJECT = 0,
    IGNORE = 1,
    NOTIFY = 2
};

/**
 * X2AP Message Type
 */
enum class X2apMessageType : uint8_t {
    INITIATING_MESSAGE = 0,
    SUCCESSFUL_OUTCOME = 1,
    UNSUCCESSFUL_OUTCOME = 2
};

/**
 * X2AP Cause Group
 */
enum class X2apCauseGroup : uint8_t {
    RADIO_NETWORK = 0,
    TRANSPORT = 1,
    PROTOCOL = 2,
    MISC = 3
};

/**
 * X2AP Information Element IDs
 */
enum class X2apIeId : uint16_t {
    OLD_ENB_UE_X2AP_ID = 10,
    NEW_ENB_UE_X2AP_ID = 9,
    CAUSE = 5,
    TARGET_CELL_ID = 11,
    GUMMEI = 17,
    UE_CONTEXT_INFORMATION = 45,
    UE_HISTORY_INFORMATION = 23,
    E_RABs_TO_BE_SETUP = 18,
    E_RABs_ADMITTED = 27,
    GLOBAL_ENB_ID = 21,
    ENB_NAME = 22,
    SERVED_CELLS = 48,
    CSG_MEMBERSHIP_STATUS = 64
};

}  // namespace asn1
}  // namespace callflow
